import org.apache.spark.sql.functions._
import java.io._
import scala.collection.mutable.ListBuffer
import org.apache.spark.sql.types._

// NOTE: Update this directory to match where your JVMs Compare Benchmark Results in CSV format are located.
val projectPath = "/Users/cguntur/projects/conf/jvms-compare-results"

val projectFileHandle = new File(projectPath)
val projectFileListing = projectFileHandle.listFiles()
val directories = projectFileListing.filter(_.isDirectory).filter(!_.isHidden)

// Use the project's template.csv to create a dataframe with the right columns. This csv file has no data.
var dummyData = spark.read.option("header", "true").csv(projectPath + "/" + "template.csv")

// Create a "sink" dataframe with no data, but right column names, so each benchmark-result can be merged onto this dataframe
var dataFrame = dummyData.toDF("Benchmark", "Class", "FullName", "Name", "Type", "Evaluation", "Collection", "Datatype", "Mode", "Threads", "Samples", "Score", "Error", "Units", "JDK", "Size")

// NOTE: Update this list if there are other or different JDKs being compared.
val jdks = List("OracleJDK", "GraalEE", "GraalCE", "AdoptOpenJDKHotspot", "AdoptOpenJDKOpenJ9", "OpenJDKHotspot", "OpenJDKGraal", "GraalEEC2")

var benchmarkExecutionResultDirectory = ""
var benchmarkExecutionDirectoryName = ""
val benchmarkDirectoryBuffer = new ListBuffer[String]()

for(directoryIndex <- 0 to (directories.length - 1)) {
    benchmarkExecutionResultDirectory = directories(directoryIndex).toString + "/benchmark-results"
    println("Using benchmark results from: " + benchmarkExecutionResultDirectory)

    var directoryPathParentOfBenchmarkResults = benchmarkExecutionResultDirectory.substring(0, benchmarkExecutionResultDirectory.lastIndexOf("benchmark-results") -1)
    var arrayOfSplitProjectPathAndExecutionDirectoryName = directoryPathParentOfBenchmarkResults.split(projectPath, 2)
    benchmarkExecutionDirectoryName = arrayOfSplitProjectPathAndExecutionDirectoryName(1).substring(1)
    println("Benchmark Execution Directory Name: " + benchmarkExecutionDirectoryName)
    benchmarkDirectoryBuffer += benchmarkExecutionDirectoryName


    // Iterate over the 01 through 08 numbered result CSV files where each prefixed number represents a run of the benchmarks with a specific JDK
    // NOTE: see the val 'jdks' that map to these numbers
    for (eachJdkIndex <- 1 to 8) {

        // Iterate over every CSV in each directory under benchmark-results for the current index (index represents a JDK)
        var csvFile = benchmarkExecutionResultDirectory+"/*/0" + eachJdkIndex + "*.csv"
        var benchmarkData = spark.read.option("header", "true").csv(csvFile)

        // Extract direct columns from the CSV data
        var df = benchmarkData.toDF("Benchmark", "Mode", "Threads", "Samples", "Score", "Error", "Units")


        // --------------------------------------------------------------------------------------------------------------------------------
        // --------------------------------------------------------------------------------------------------------------------------------
        //                                             --- BEGIN: DERIVED DATA - NOT FROM CSV ---
        //                                                  --------------------------------

        // Add a JDK column with values that are sequentially listed in the jdks val above. 
        // JDKs map to the prefixed number in the results.
        // For instance, OracleJDK8 is the first run and its prefix for results is 01.
        df = df.withColumn("JDK", when($"Mode".isNull, "").otherwise(jdks(eachJdkIndex - 1)))

        // Add a Benchmark column that extracts its value from the CSV Benchmark column
        // The first 23 characters represent the package name of the Benchmark class, we discard that.
        // The 200 is an arbitrary number assuming the fully qualified name of the benchmark class is 500 or less characters
        df = df.withColumn("Benchmark", trim(substring($"Benchmark", 23, 500)))

        // Add a size column that holds the size of the data tested in the benchmark (derived from the directory name)
        df = df.withColumn("Size", when($"Mode".isNull, "").otherwise(benchmarkExecutionDirectoryName))

        // Add a Class column that holds the name of the Benchmark class
        df = df.withColumn("Class", split($"Benchmark", "\\.").getItem(0))
        // Add a Fullnmae column that holds the method of the Benchmark class
        df = df.withColumn("FullName", split($"Benchmark", "\\.").getItem(1))

        // Derived from the Benchmark method name
        // --------------------------------------
        // Add a Name column that holds the operation name of the benchmark
        df = df.withColumn("Name", split($"FullName", "_").getItem(0))
        // Add a Collection column that holds the collection framework used (EC = Eclipse Collections, JDK = Java Collections fromaework)
        df = df.withColumn("Collection", split($"FullName", "_").getItem(1))
        // Add a Collection column that holds the datatype of items in te benchmarked method (Object, Boxed or Primitive)
        df = df.withColumn("Datatype", when($"Benchmark".contains("Person"), "Object").otherwise(split($"FullName", "_").getItem(2)))
        df = df.withColumn("Evaluation", when($"Benchmark".contains("Person"), split($"FullName", "_").getItem(2)).otherwise(split($"FullName", "_").getItem(3)))
        df = df.withColumn("Type", when($"Benchmark".contains("Person"), split($"FullName", "_").getItem(3)).otherwise(split($"FullName", "_").getItem(4)))

        df = df.withColumn("Score", $"Score".cast(DoubleType))
        df = df.withColumn("Error", $"Error".cast(DoubleType))

        df = df.select("Benchmark", "Class", "FullName", "Name", "Type", "Evaluation", "Collection", "Datatype", "Mode", "Threads", "Samples", "Score", "Error", "Units", "JDK", "Size")

        //                                                  ------------------------------
        //                                                --- END: DERIVED NOT FROM CSV ---
        // --------------------------------------------------------------------------------------------------------------------------------
        // --------------------------------------------------------------------------------------------------------------------------------



        // Merge the data collected from each CSV into the "sink" dataframe
        dataFrame = dataFrame.union(df)
    }
}

// Generate (or replace) a temporary view with the name 'benchmarks' that can now be queried.
dataFrame.createOrReplaceTempView("benchmarks")

val benchmarkDirectories = benchmarkDirectoryBuffer.toList
val benchmarkDirectoriesDF = benchmarkDirectories.toDF("Directories")
benchmarkDirectoriesDF.createOrReplaceTempView("directories")
